    function swapAmountIn(
        address _tokenIn,
        address _tokenOut,
        uint256 _amount,
        address _siloOracle,
        address _siloAsset
    ) external override returns (uint256 amountOut) {
        bytes32 poolId = resolvePoolId(_siloOracle, _siloAsset);
        return _swapAmountIn(_tokenIn, _tokenOut, _amount, poolId);
    }

