// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import "./interfaces/IBaseSilo.sol";
import "./interfaces/IGuardedLaunch.sol";
import "./interfaces/IRepository.sol";
import "./interfaces/ISiloOracleRepository.sol";
import "./interfaces/IInterestRateModel.sol";
import "./interfaces/IShareToken.sol";

import "./lib/EasyMath.sol";
import "./Error.sol";

abstract contract BaseSilo is IBaseSilo, ReentrancyGuard {
    using SafeERC20 for ERC20;
    using EasyMath for uint256;

    /// @dev is value that used for integer calculations and decimal points for utilisation ratios, LTV, protocol fees
    uint256 public constant PRECISION_DECIMALS = 1e18;
    uint256 public constant INFINITY = type(uint256).max;

    // solhint-disable-next-line var-name-mixedcase
    uint256 public immutable ASSET_DECIMAL_POINTS;
    IRepository immutable public repository;
    // asset address for which Silo was created
    address public immutable siloAsset;

    // asset => AssetStorage
    mapping(address => AssetStorage) public state;

    event Deposit(address indexed asset, address indexed depositor, uint256 amount, bool collateralOnly);
    event Withdraw(
        address indexed asset,
        address indexed depositor,
        address indexed receiver,
        uint256 amount,
        bool collateralOnly
    );
    event Borrow(address indexed asset, address indexed user, uint256 amount);
    event Repay(address indexed asset, address indexed user, uint256 amount);
    event Liquidate(address indexed asset, address indexed _user, uint256 amountRepaid, uint256 seizedCollateral);

    modifier onlyExistingAsset(address _asset) {
        require(address(state[_asset].collateralToken) != address(0), "BaseSilo: asset does not exist");
        _;
    }

    modifier onlyRouter() {
        require(msg.sender == repository.router(), "BaseSilo: onlyRouter");
        _;
    }

    modifier applyInterestsBefore(address _asset) {
        _accrueInterest(_asset, block.timestamp);
        _;
    }

    modifier onlyWhenAssetActive(address _asset) {
        require(!state[_asset].removed, "BaseSilo: asset removed");
        _;
    }

    modifier siloPositionSolvent(address _user) {
        _;
        require(isSolvent(_user), "BaseSilo: not solvent in silo position");
    }

    modifier validateBorrow(address _user) {
        _;
        require(getLTV(_user) <= calculateUserLTV(_user, true), "BaseSilo: maximumLTV");
    }

    modifier validateMaxDepositsAfter(address _asset) {
        _;

        ISiloOracleRepository oracle = ISiloOracleRepository(repository.oracle());

        // TODO: refactor getMaxSiloBridgeDeposits
        require(
            oracle.getPrice(_asset) * state[_asset].totalDeposits / (10 ** ERC20(_asset).decimals()) <=
            IGuardedLaunch(address(repository)).getMaxSiloBridgeDeposits(address(this)),
            "BaseSilo: total deposits exceed liquidity limit"
        );
    }

    constructor (address _repository, address _siloAsset) {
        repository = IRepository(_repository);
        siloAsset = _siloAsset;
        ASSET_DECIMAL_POINTS = 10**ERC20(_siloAsset).decimals();
    }

    function syncBridgeAssets() external {
        // sync removed assets
        address[] memory removedBridgeAssets = repository.getRemovedBridgeAssets();

        for (uint256 i = 0; i < removedBridgeAssets.length; i++) {
            // If removed bridge asset is a silo asset for this silo, do not remove it
            if (removedBridgeAssets[i] != siloAsset) {
                state[removedBridgeAssets[i]].removed = true;
            }
        }

        // init silo asset if needed
        if (address(state[siloAsset].collateralToken) == address(0)) {
            _initAsset(repository.tokensFactory(), siloAsset, false);
        }

        // sync active assets
        address[] memory bridgeAssets = repository.getBridgeAssets();

        for (uint256 i = 0; i < bridgeAssets.length; i++) {
            state[bridgeAssets[i]].removed = false;

            // In case a bridge asset is added that already has a Silo,
            // do not initiate that asset in its Silo
            if (address(state[bridgeAssets[i]].collateralToken) == address(0)) {
                _initAsset(repository.tokensFactory(), bridgeAssets[i], true);
            }
        }
    }

    function assetStorage(address _asset) external view override returns (AssetStorage memory) {
        return state[_asset];
    }

    /// @dev Yearly interest rate for borrowing asset token, dynamically calculated for current block timestamp
    function interestRateAPY(address _asset) external view returns (uint256) {
        return _getModel(_asset).getCurrentInterestRate(
            address(this),
            _asset,
            block.timestamp
        );
    }

    function interestRateTimestamp(address _asset) external view override returns (uint256) {
        return state[_asset].interestRateTimestamp;
    }

    function getUtilization(address _asset) external view override returns (uint256) {
        uint256 totalBorrowAmountCached = state[_asset].totalBorrowAmount;
        uint256 totalDepositsCached = state[_asset].totalDeposits;
        if (totalDepositsCached == 0 || totalBorrowAmountCached == 0) return 0;

        // solhint-disable-next-line var-name-mixedcase
        uint256 DP = uint256(_getModel(_asset).DP());
        return totalBorrowAmountCached * DP / totalDepositsCached;
    }

    /// @dev returns total deposits with interest dynamically calculated at current block timestamp
    function totalDepositsWithInterest(address _asset) public view returns (uint256 _totalDeposits) {
        uint256 rcomp = _getModel(_asset).getCompoundInterestRate(address(this), _asset, block.timestamp);

        uint256 protocolShareFee = repository.protocolShareFee();
        uint256 depositorsShare = PRECISION_DECIMALS - protocolShareFee;
        uint256 assetDeposits = state[_asset].totalDeposits;

        return assetDeposits + assetDeposits * rcomp * depositorsShare / PRECISION_DECIMALS / PRECISION_DECIMALS;
    }

    function getAssets() public view override returns (address[] memory assets) {
        address[] memory bridgeAssets = repository.getBridgeAssets();
        address[] memory removedBridgeAssets = repository.getRemovedBridgeAssets();
        uint256 allAssetsLength = bridgeAssets.length + removedBridgeAssets.length + 1;
        assets = new address[](allAssetsLength);

        for (uint256 i = 0; i < bridgeAssets.length; i++) {
            assets[i] = bridgeAssets[i];
        }

        for (uint256 i = bridgeAssets.length; i < bridgeAssets.length + removedBridgeAssets.length; i++) {
            assets[i] = bridgeAssets[i];
        }

        assets[allAssetsLength - 1] = siloAsset;
    }

    function isSolvent(address _user) public view override returns (bool) {
        require(_user != address(0), "BaseSilo: user != address(0)");

        uint256 liquidationThreshold = calculateUserLTV(_user, false);
        uint256 ltv = getLTV(_user);
        return ltv <= liquidationThreshold;
    }

    /// Loan-To-Value
    function getLTV(address _user) public view override returns (uint256) {
        address[] memory assets = getAssets();
        uint256 collateralValue;
        uint256 borrowAmountValue;
        ISiloOracleRepository oracle = ISiloOracleRepository(repository.oracle());

        for (uint256 i = 0; i < assets.length; i++) {
            uint256 assetPrice = oracle.getPrice(assets[i]);
            uint256 decimals = ERC20(assets[i]).decimals();

            uint256 _borrowAmountValue = calculateBorrowValue(_user, assets[i], assetPrice, decimals);

            // It should be impossible but never too safe. If a user somehow ends up with debt
            // and collateral in single asset, we ignore collateral value of that asset
            if (_borrowAmountValue > 0) {
                borrowAmountValue += _borrowAmountValue;
            } else {
                // We allow removed bridge asset to be a collateral, that's why we do not check for:
                // !state[assets[i]].removed
                // otherwise people may be liquidated right away.
                collateralValue += calculateCollateralValue(_user, assets[i], assetPrice, decimals);
            }
        }

        if (borrowAmountValue == 0) return 0;
        if (collateralValue == 0) return INFINITY;

        return borrowAmountValue * PRECISION_DECIMALS / collateralValue;
    }

    /// @dev value of collateral (asset) provided by user
    function calculateCollateralValue(address _user, address _asset, uint256 _assetPrice, uint256 _assetDecimals)
        public
        view
        override
        returns (uint256)
    {
        AssetStorage storage _state = state[_asset];

        uint256 assetAmount = _state.collateralToken.balanceOf(_user).toAmount(
            totalDepositsWithInterest(_asset),
            _state.collateralToken.totalSupply()
        );

        uint256 assetCollateralOnlyAmount = _state.collateralOnlyToken.balanceOf(_user).toAmount(
            _state.collateralOnlyDeposits,
            _state.collateralOnlyToken.totalSupply()
        );

        return (assetAmount + assetCollateralOnlyAmount) * _assetPrice / (10 ** _assetDecimals);
    }

    /// @dev Amount of token that is available for borrowing.
    /// @return Silo liquidity
    function liquidity(address _asset) public view returns (uint256) {
        return ERC20(_asset).balanceOf(address(this)) - state[_asset].collateralOnlyDeposits;
    }

    function calcFee(uint256 _amount) public view returns (uint256) {
        uint256 entryFee = repository.entryFee();
        if (entryFee == 0) return 0; // no fee

        return _amount * entryFee / PRECISION_DECIMALS;
    }

    function calculateBorrowValue(address _user, address _asset, uint256 _assetPrice, uint256 _assetDecimals)
        public
        view
        returns (uint256)
    {
        uint256 debtAmount = getBorrowAmount(_asset, _user, block.timestamp);
        return debtAmount * _assetPrice / (10 ** _assetDecimals);
    }

    /// @return assetDebtAmount total amount of asset user needs to repay at provided timestamp
    function getBorrowAmount(address _asset, address _user, uint256 _timestamp) public view returns (uint256) {
        uint256 rcomp = _getModel(_asset).getCompoundInterestRate(address(this), _asset, _timestamp);
        uint256 totalBorrowAmountCached = state[_asset].totalBorrowAmount;
        totalBorrowAmountCached += totalBorrowAmountCached * rcomp / PRECISION_DECIMALS;

        return state[_asset].debtToken.balanceOf(_user).toAmount(
            totalBorrowAmountCached,
            state[_asset].debtToken.totalSupply()
        );
    }

    /// @inheritdoc IBaseSilo
    function depositPossible(address _asset, address _depositor) public view override returns (bool) {
        return state[_asset].debtToken.balanceOf(_depositor) == 0;
    }

    /// @inheritdoc IBaseSilo
    function borrowPossible(address _asset, address _borrower) public view override returns (bool) {
        return state[_asset].collateralToken.balanceOf(_borrower) == 0
            && state[_asset].collateralOnlyToken.balanceOf(_borrower) == 0;
    }

    /// @dev calculate combined Loan-To-Value of user, using either maximumLTV or liquidationThreshold
    /// @return Loan-To-Value of given user
    function calculateUserLTV(address _user, bool useMaximumLTV) public view override returns (uint256) {
        uint256 totalCollateralValue;
        uint256 totalAvailableToBorrow;
        ISiloOracleRepository oracle = ISiloOracleRepository(repository.oracle());

        address[] memory assets = getAssets();

        for (uint256 i = 0; i < assets.length; i++) {
            uint256 assetPrice = oracle.getPrice(assets[i]);
            uint256 decimals = ERC20(assets[i]).decimals();

            uint256 deposit = state[assets[i]].collateralToken.balanceOf(_user) +
            state[assets[i]].collateralOnlyToken.balanceOf(_user);

            // do math only if user has collateral deposited for given asset
            if (deposit != 0) {
                uint256 collateralValue = calculateCollateralValue(_user, assets[i], assetPrice, decimals);
                uint256 assetLTV = useMaximumLTV ?
                repository.getMaximumLTV(address(this), assets[i]) :
                repository.getLiquidationThreshold(address(this), assets[i]);
                // value that can be borrowed against the deposit
                // ie. for assetLTV = 50%, 1 ETH * 50% = 0.5 ETH of available to borrow
                uint256 availableToBorrow = collateralValue * assetLTV / PRECISION_DECIMALS;

                totalCollateralValue += collateralValue;
                totalAvailableToBorrow += availableToBorrow;
            }
        }

        if (totalAvailableToBorrow == 0) return 0;
        if (totalCollateralValue == 0) return INFINITY;

        return totalAvailableToBorrow * PRECISION_DECIMALS / totalCollateralValue;
    }

    /// @dev Initiate asset by deploying accounting EC20 tokens for collateral and debt
    function _initAsset(ITokensFactory _tokensFactory, address _asset, bool _isBridgeAsset) internal {
        // Naming convention in UNI example:
        // - for siloAsset: SiloUNI, Debt-SiloUNI, SiloUNI-collateral-ONLY
        // - for bridgeAsset: SiloUNI-ETH, Debt-SiloUNI-ETH, SiloUNI-ETH-collateral-ONLY
        string memory collateralName;
        string memory debtName;
        string memory assetSymbol = ERC20(_asset).symbol();

        if (_isBridgeAsset) {
            string memory baseSymbol = state[siloAsset].collateralToken.symbol();
            collateralName = string(abi.encodePacked(baseSymbol, string("-"), assetSymbol));
        } else {
            string memory baseSymbol = "Silo";
            collateralName = string(abi.encodePacked(baseSymbol, assetSymbol));
        }
        string memory collateralOnlyName = string(abi.encodePacked(collateralName, string("-collateral-ONLY")));

        debtName = string(abi.encodePacked(string("Debt-"), collateralName));

        state[_asset].collateralToken = _tokensFactory.createShareCollateralToken(
            collateralName, collateralName, _asset
        );

        state[_asset].collateralOnlyToken = _tokensFactory.createShareCollateralToken(
            collateralOnlyName, collateralOnlyName, _asset
        );

        state[_asset].debtToken = _tokensFactory.createShareDebtToken(debtName, debtName, _asset);
    }

    function _deposit(
        address _asset,
        address _from,
        address _depositor,
        uint256 _amount,
        bool _collateralOnly
    )
        internal
        nonReentrant
        onlyExistingAsset(_asset)
        onlyWhenAssetActive(_asset)
        applyInterestsBefore(_asset)
        validateMaxDepositsAfter(_asset)
    {
        if (!depositPossible(_asset, _depositor)) {
            revert DepositNotPossible(_asset, _depositor);
        }

        AssetStorage storage _state = state[_asset];

        uint256 balanceBefore = ERC20(_asset).balanceOf(address(this));
        ERC20(_asset).safeTransferFrom(_from, address(this), _amount);
        uint256 balanceAfter = ERC20(_asset).balanceOf(address(this));

        _amount = balanceAfter - balanceBefore;
        uint256 totalDepositsCached = _collateralOnly ? _state.collateralOnlyDeposits : _state.totalDeposits;

        if (_collateralOnly) {
            uint256 share = _amount.toShare(totalDepositsCached, _state.collateralOnlyToken.totalSupply());
            _state.collateralOnlyDeposits = totalDepositsCached + _amount;
            _state.collateralOnlyToken.mint(_depositor, share);
        } else {
            uint256 share = _amount.toShare(totalDepositsCached, _state.collateralToken.totalSupply());
            _state.totalDeposits = totalDepositsCached + _amount;
            _state.collateralToken.mint(_depositor, share);
        }

        emit Deposit(_asset, _depositor, _amount, _collateralOnly);
    }

    function _borrow(address _asset, address _borrower, address _receiver, uint256 _amount)
        internal
        nonReentrant
        onlyExistingAsset(_asset)
        onlyWhenAssetActive(_asset)
        validateBorrow(_borrower)
        applyInterestsBefore(_asset)
    {
        if (!borrowPossible(_asset, _borrower)) {
            revert("BorrowNotPossible");
        }

        AssetStorage storage _state = state[_asset];
        require(liquidity(_asset) >= _amount, "BaseSilo: deposit shortage");

        uint256 fee = calcFee(_amount);
        uint256 borrowerShare = (_amount + fee).toShare(_state.totalBorrowAmount, _state.debtToken.totalSupply());

        _state.debtToken.mint(_borrower, borrowerShare);
        _state.totalBorrowAmount += _amount + fee;
        _state.protocolFees += fee;

        emit Borrow(_asset, _borrower, _amount);

        ERC20(_asset).safeTransfer(_receiver, _amount);
    }

    function _flashUserLiquidation(address[] memory _allSiloAssets, address _borrower, address _liquidator)
        internal
        returns (uint256[] memory receivedCollaterals, uint256[] memory amountsToRepaid)
    {
        uint256 blockTimestamp = block.timestamp;
        uint256 assetsLength = _allSiloAssets.length;
        receivedCollaterals = new uint256[](assetsLength);
        amountsToRepaid = new uint256[](assetsLength);

        for (uint256 i = 0; i < assetsLength; i++) {
            AssetStorage storage _state = state[_allSiloAssets[i]];

            _accrueInterest(_allSiloAssets[i], blockTimestamp);
            // we do not allow for partial repayment on liquidation, that's why max
            (amountsToRepaid[i],) = _calculateAmountAndShare(_state, _borrower, type(uint256).max);


            (, uint256 withdrawnOnlyAmount) = _withdrawAsset(
                _allSiloAssets[i],
                _state.collateralOnlyDeposits,
                type(uint256).max,
                _state.collateralOnlyToken,
                _borrower,
                _liquidator
            );

            (,uint256 withdrawnAmount) = _withdrawAsset(
                _allSiloAssets[i],
                _state.totalDeposits,
                type(uint256).max,
                _state.collateralToken,
                _borrower,
                _liquidator
            );

            receivedCollaterals[i] = withdrawnOnlyAmount + withdrawnAmount;
        }
    }

    /// @dev harvest protocol fees from particular asset
    /// @param _asset asset we want to harvest fees from
    /// @param _receiver address of fees receiver
    function _harvestProtocolFees(address _asset, address _receiver) internal {
        AssetStorage storage _state = state[_asset];
        uint256 protocolFeesCached = _state.protocolFees;
        uint256 availableFees = protocolFeesCached - _state.harvestedProtocolFees;
        if (availableFees == 0) return;

        _state.harvestedProtocolFees = protocolFeesCached;
        ERC20(_asset).safeTransfer(_receiver, availableFees);
    }

    /// @param _asset address
    /// @param _blockTimestamp current timestamp
    function _accrueInterest(address _asset, uint256 _blockTimestamp) internal {
        AssetStorage storage _state = state[_asset];

        if (_blockTimestamp == _state.interestRateTimestamp) {
            return;
        }

        uint256 rcomp = _getModel(_asset).getCompoundInterestRateAndUpdate(_asset, _blockTimestamp);
        uint256 protocolShareFee = repository.protocolShareFee();

        uint256 totalBorrowAmountCached = _state.totalBorrowAmount;

        uint256 totalInterest = totalBorrowAmountCached * rcomp / PRECISION_DECIMALS;
        uint256 protocolShare = totalInterest * protocolShareFee / PRECISION_DECIMALS;
        uint256 depositorsShare = totalInterest - protocolShare;

        // update contract state
        _state.totalBorrowAmount = totalBorrowAmountCached + totalInterest;
        _state.totalDeposits = _state.totalDeposits + depositorsShare;
        _state.protocolFees = _state.protocolFees + protocolShare;
        _state.interestRateTimestamp = _blockTimestamp;
    }

    function _withdraw(
        address _asset,
        address _depositor,
        address _receiver,
        uint256 _amount,
        bool _collateralOnly
    )
        internal
        nonReentrant // because we transfering tokens
        onlyExistingAsset(_asset)
        siloPositionSolvent(_depositor)
        applyInterestsBefore(_asset)
        returns (uint256 amount)
    {
        AssetStorage storage _state = state[_asset];
        uint256 burnedShare;

        if (_collateralOnly) {
            uint256 collateralOnlyDepositsCached = _state.collateralOnlyDeposits;

            (burnedShare, _amount) = _withdrawAsset(
                _asset,
                collateralOnlyDepositsCached,
                _amount,
                _state.collateralOnlyToken,
                _depositor,
                _receiver
            );

            _state.collateralOnlyDeposits = collateralOnlyDepositsCached - _amount;
        } else {
            uint256 totalDepositsCached = _state.totalDeposits;

            (burnedShare, _amount) = _withdrawAsset(
                _asset,
                totalDepositsCached,
                _amount,
                _state.collateralToken,
                _depositor,
                _receiver
            );

            _state.totalDeposits = totalDepositsCached - _amount;
        }

        emit Withdraw(_asset, _depositor, _receiver, _amount, _collateralOnly);

        return _amount;
    }

    function _withdrawAsset(
        address _asset,
        uint256 _assetTotalDeposits,
        uint256 _assetAmount,
        IShareToken _shareToken,
        address _depositor,
        address _receiver
    )
        internal
        returns (uint256 burnedShare, uint256 withdrawnAmount)
    {
        if (_assetAmount == type(uint256).max) {
            burnedShare = _shareToken.balanceOf(_depositor);
            withdrawnAmount = burnedShare.toAmount(_assetTotalDeposits, _shareToken.totalSupply());
        } else {
            burnedShare = _assetAmount.toShare(_assetTotalDeposits, _shareToken.totalSupply());
            withdrawnAmount = _assetAmount;
        }

        if (withdrawnAmount == 0) return (0, 0);

        _shareToken.burn(_depositor, burnedShare);

        // in case token sent in fee-on-transfer type of token we do not care when withdrawing
        ERC20(_asset).safeTransfer(_receiver, withdrawnAmount);
    }

    /// @dev gets interest rates model object
    /// @param _asset asset for which to calculate interest rate
    /// @return IInterestRateModel interest rates model object
    function _getModel(address _asset) internal view returns (IInterestRateModel) {
        return IInterestRateModel(repository.getInterestRateModel(address(this), _asset));
    }

    /// @dev calculates amount to repay based on user shares,
    ///         we do not apply virtual balances here, if needed, thay need to be apply beforehand
    /// @param _assetStorage asset storage
    /// @param _borrower borrower address
    /// @param _amount proposed amount to repay, based on that
    /// @return amount amount to repay
    /// @return repayShare amount to repay as share
    function _calculateAmountAndShare(AssetStorage storage _assetStorage, address _borrower, uint256 _amount)
        internal
        view
        returns (uint256 amount, uint256 repayShare)
    {
        uint256 borrowerDebtShare = _assetStorage.debtToken.balanceOf(_borrower);
        uint256 debtTokenTotalSupply = _assetStorage.debtToken.totalSupply();
        uint256 totalBorrowed = _assetStorage.totalBorrowAmount;

        if (_amount == type(uint256).max) {
            repayShare = borrowerDebtShare;
            amount = repayShare.toAmount(totalBorrowed, debtTokenTotalSupply);
        } else {
            uint256 maxAmount = borrowerDebtShare.toAmount(totalBorrowed, debtTokenTotalSupply);

            if (_amount <= maxAmount) {
                repayShare = _amount.toShare(totalBorrowed, debtTokenTotalSupply);
                amount = _amount;
            } else {
                repayShare = borrowerDebtShare;
                amount = maxAmount;
            }
        }
    }
}

