contracts/Silo.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "./interfaces/ISilo.sol";

import "./lib/EasyMath.sol";
import "./BaseSilo.sol";

contract Silo is ISilo, BaseSilo {
    using SafeERC20 for ERC20;
    using EasyMath for uint256;

    constructor (address _repository, address _siloAsset) BaseSilo(_repository, _siloAsset) {}

    /// @notice Deposit `_amount` of `_asset` tokens from `msg.sender` to the Silo
    /// @param _asset The address of the token to deposit
    /// @param _amount The amount of the token to deposit
    /// @param _collateralOnly True if depositing collateral only
    function deposit(address _asset, uint256 _amount, bool _collateralOnly) external override {
        _deposit(_asset, msg.sender, msg.sender, _amount, _collateralOnly);
    }

    /// @notice Router function to deposit `_amount` of `_asset` tokens to the Silo for the `_depositor`
    /// @param _asset The address of the token to deposit
    /// @param _depositor The address of the recipient of collateral tokens
    /// @param _amount The amount of the token to deposit
    /// @param _collateralOnly True if depositing collateral only
    function depositFor(
        address _asset,
        address _depositor,
        uint256 _amount,
        bool _collateralOnly
    ) external override onlyRouter {
        _deposit(_asset, msg.sender, _depositor, _amount, _collateralOnly);
    }

    /// @notice Withdraw `_amount` of `_asset` tokens from the Silo to `msg.sender`
    /// @param _asset The address of the token to withdraw
    /// @param _amount The amount of the token to withdraw
    /// @param _collateralOnly True if withdrawing collateral only deposit
    function withdraw(address _asset, uint256 _amount, bool _collateralOnly) external override {
        _withdraw(_asset, msg.sender, msg.sender, _amount, _collateralOnly);
    }

    /// @notice Router function to withdraw `_amount` of `_asset` tokens from the Silo for the `_depositor`
    /// @param _asset The address of the token to withdraw
    /// @param _depositor The address of the collateral tokens source account
    /// @param _receiver The address of the asset receiver
    /// @param _amount The amount of the token to withdraw
    /// @param _collateralOnly True if withdrawing collateral only deposit
    function withdrawFor(address _asset, address _depositor, address _receiver, uint256 _amount, bool _collateralOnly)
        external
        override
        onlyRouter
    {
        _withdraw(_asset, _depositor, _receiver, _amount, _collateralOnly);
    }

    /// @notice Borrow `_amount` of `_asset` tokens from the Silo to `msg.sender`
    /// @param _asset The address of the token to borrow
    /// @param _amount The amount of the token to borrow
    function borrow(address _asset, uint256 _amount) external override {
        _borrow(_asset, msg.sender, msg.sender, _amount);
    }

    /// @notice Router function to borrow `_amount` of `_asset` tokens from the Silo for the `_receiver`
    /// @param _asset The address of the token to borrow
    /// @param _borrower The address of the debt tokens receiver
    /// @param _receiver The address of the asset receiver
    /// @param _amount The amount of the token to borrow
    function borrowFor(address _asset, address _borrower, address _receiver, uint256 _amount)
        external
        override
        onlyRouter
    {
        _borrow(_asset, _borrower, _receiver, _amount);
    }

    /// @notice Repay `_amount` of `_asset` tokens from `msg.sender` to the Silo
    /// @param _asset The address of the token to repay
    /// @param _amount amount of asset to repay, includes interests
    function repay(address _asset, uint256 _amount) external override {
        _repay(_asset, msg.sender, msg.sender, _amount);
    }

    /// @notice Allows to repay in behalf of borrower to execute liquidation
    /// @param _asset The address of the token to repay
    /// @param _borrower The address of the user to have debt tokens burned
    /// @param _amount amount of asset to repay, includes interests
    function repayFor(address _asset, address _borrower, uint256 _amount) external override {
        require(!isSolvent(_borrower), "Silo.repayFor: borrower is solvent");

        _repay(_asset, _borrower, msg.sender, _amount);
    }

    /// @inheritdoc ISilo
    function flashLiquidate(
        address[] memory _users,
        address _liquidator,
        IFlashLiquidationReceiver _flashReceiver,
        bytes memory _flashReceiverData
    )
        external
        override
        returns (
            address[] memory assets,
            uint256[][] memory receivedCollaterals,
            uint256[][] memory shareAmountsToRepay
        )
    {
        assets = getAssets();
        uint256 usersLength = _users.length;
        receivedCollaterals = new uint256[][](usersLength);
        shareAmountsToRepay = new uint256[][](usersLength);

        for (uint256 i = 0; i < usersLength; i++) {
            (
                receivedCollaterals[i],
                shareAmountsToRepay[i]
            ) = _userLiquidation(assets, _users[i], _liquidator, _flashReceiver, _flashReceiverData);
        }
    }

    /// @dev harvest protocol fees from an array of assets
    /// @param _assets array of assets we want to harvest fees from
    function harvestProtocolFees(address[] calldata _assets) external {
        address repositoryOwner = repository.owner();

        for (uint256 i = 0; i < _assets.length; i++) {
            _harvestProtocolFees(_assets[i], repositoryOwner);
        }
    }

    /// @notice Function to update interests for `_asset` token since the last saved state
    /// @param _asset The address of the token to be updated
    function accrueInterest(address _asset) public {
        _accrueInterest(_asset, block.timestamp);
    }

    /// @param _assets all currant assets, this is optimisation, so we don't have to read it from storage few times
    /// @param _user user to liquidate
    /// @param _liquidator address which will get all collaterals
    /// @param _flashReceiver address which will be notified once collaterals will be send
    /// @param _flashReceiverData custom data to forward to receiver
    /// @return receivedCollaterals amounts of collaterals transfered to liquidator
    /// @return shareAmountsToRepay expected amounts to repay
    function _userLiquidation(
        address[] memory _assets,
        address _user,
        address _liquidator,
        IFlashLiquidationReceiver _flashReceiver,
        bytes memory _flashReceiverData
    )
        internal
        nonReentrant // because of _flashReceiver.siloLiquidationCallback
        returns (uint256[] memory receivedCollaterals, uint256[] memory shareAmountsToRepay)
    {
        // gracefully fail if _user is solvent
        if (isSolvent(_user)) {
            uint256[] memory empty = new uint256[](_assets.length);
            return (empty, empty);
        }

        (receivedCollaterals, shareAmountsToRepay) = _flashUserLiquidation(_assets, _user, _liquidator);

        // _flashReceiver needs to repayFor user
        _flashReceiver.siloLiquidationCallback(
            _user,
            _assets,
            receivedCollaterals,
            shareAmountsToRepay,
            _flashReceiverData
        );

        for (uint256 i = 0; i < _assets.length; i++) {
            if (receivedCollaterals[i] != 0 || shareAmountsToRepay[i] != 0) {
                emit Liquidate(_assets[i], _user, shareAmountsToRepay[i], receivedCollaterals[i]);
            }
        }

        require(isSolvent(_user), "Silo: user not solvent");
    }

    function _repay(address _asset, address _borrower, address _repayer, uint256 _amount)
        internal
        onlyExistingAsset(_asset)
        applyInterestsBefore(_asset)
        returns (uint256)
    {
        AssetStorage storage _assetStorage = state[_asset];
        uint256 repayShare;

        (_amount, repayShare) = _calculateAmountAndShare(_assetStorage, _borrower, _amount);

        if (repayShare == 0) {
            return 0;
        }

        emit Repay(_asset, _borrower, _amount);

        // we checking balances in case of fee on transfer tokens or any other weird stuff
        uint256 balanceBefore = ERC20(_asset).balanceOf(address(this));
        ERC20(_asset).safeTransferFrom(_repayer, address(this), _amount);
        uint256 balanceAfter = ERC20(_asset).balanceOf(address(this));
        require(balanceAfter - balanceBefore >= _amount, "Silo: transferred amount is lower that required amount");

        // we do not using reentrancy, so just in case change status about debt after token transfer
        _assetStorage.debtToken.burn(_borrower, repayShare);
        _assetStorage.totalBorrowAmount -= _amount;

        return _amount;
    }
}

contracts/interfaces/ISilo.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import "./IBaseSilo.sol";
import "./IFlashLiquidationReceiver.sol";

interface ISilo is IBaseSilo {
    function borrow(address _asset, uint256 _amount) external;

    function borrowFor(address _asset, address _user, address _to, uint256 _amount) external;

    function deposit(address _asset, uint256 _amount, bool collateralOnly) external;

    function depositFor(address _asset, address _user, uint256 _amount, bool collateralOnly) external;

    function repay(address _asset, uint256 _amount) external;

    function repayFor(address _asset, address _user, uint256 _amount) external;

    function withdraw(address _asset, uint256 _amount, bool collateralOnly) external;

    function withdrawFor(
        address _asset,
        address _depositor,
        address _receiver,
        uint256 _amount,
        bool collateralOnly
    ) external;

    /// @notice this methods does not requires to have tokens in order to liquidate user
    /// @param _users array of users to liquidate
    /// @param _liquidator all collateral will be send to this address
    /// @param _flashReceiver this address will be notified once all collateral will be send to _liquidator
    /// @param _flashReceiverData this data will be forward to receiver on notification
    /// @return assets array of all processed assets (collateral + debt, including removed)
    /// @return receivedCollaterals seizedCollaterals[userId][assetId] => amount
    ///         amounts of collaterals send to liquidator
    /// @return shareAmountsToRepaid shareAmountsToRepaid[userId][assetId] => amount
    ///         required amounts of debt to be repaid
    function flashLiquidate(
        address[] memory _users,
        address _liquidator,
        IFlashLiquidationReceiver _flashReceiver,
        bytes memory _flashReceiverData
    )
        external
        returns (
            address[] memory assets,
            uint256[][] memory receivedCollaterals,
            uint256[][] memory shareAmountsToRepaid
        );
}

contracts/interfaces/IFlashLiquidationRe.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

/// @dev when performing Silo flash liquidation, FlashReceiver contract will receive all collaterals
interface IFlashLiquidationReceiver {
    /// @dev this method is called when doing Silo flash liquidation
    ///         one can NOT assume, that if _seizedCollateral[i] != 0, then _shareAmountsToRepaid[i] must be 0
    ///         one should assume, that any combination of amounts is possible
    ///         on callback, one must call `Silo.repayFor` because at the end of transaction,
    ///         Silo will check if borrower is solvent.
    /// @param _user user address, that is liquidated
    /// @param _assets array of collateral assets received during user liquidation
    ///         this array contains all assets (collateral borrowed) without any order
    /// @param _receivedCollaterals array of collateral amounts received during user liquidation
    ///         indexes of amounts are related to `_assets`,
    /// @param _shareAmountsToRepaid array of amounts to repay for each asset
    ///         indexes of amounts are related to `_assets`,
    /// @param _flashReceiverData data that are passed from sender that executes liquidation
    function siloLiquidationCallback(
        address _user,
        address[] calldata _assets,
        uint256[] calldata _receivedCollaterals,
        uint256[] calldata _shareAmountsToRepaid,
        bytes memory _flashReceiverData
    ) external;
}

contracts/Repository.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./interfaces/IFactory.sol";
import "./interfaces/IRepository.sol";
import "./interfaces/ISiloOracleRepository.sol";

import "./utils/GuardedLaunch.sol";
import "./Silo.sol";
import "./Error.sol";
import "./interfaces/ITokensFactory.sol";

/* solhint-disable max-states-count */
contract Repository is IRepository, GuardedLaunch {
    /// @dev AssetConfig struct represents configurable parameters for each Silo
    struct AssetConfig {
        /// @dev Loan-to-Value ratio represents the maximum borrowing power of a specific collateral.
        ///      For example, if a collateral has a LTV of 75%, the user can borrow up to 0.75 worth
        ///      of ETH in the principal currency for every 1 ETH worth of collateral.
        uint256 maxLoanToValue;

        /// @dev Liquidation Threshold represents the threshold at which a borrow position will be considered
        ///      undercollateralized and subject to liquidation for each collateral. For example,
        ///      if a collateral has a liquidation threshold of 80%, it means that the loan will be
        ///      liquidated when the borrowAmount value is worth 80% of the collateral value.
        uint256 liquidationThreshold;

        /// @dev interest rate model address
        address interestRateModel;
    }

    /// @dev Default values for AssetConfig. Used if values are not configured manually.
    AssetConfig public defaultAssetConfig;

    /// @dev List of bridge assets supported by the protocol
    address[] public bridgeAssets;

    /// @dev List of bridge assets removed by the protocol
    address[] public removedBridgeAssets;

    /// @dev Index mapping for bridgeAssets array
    mapping(address => uint256) public bridgeAssetsIndex;

    /// @dev Index mapping for removedBridgeAssets array
    mapping(address => uint256) public removedBridgeAssetsIndexes;

    /// @dev one time protocol fee for opening a borrow position in precision points
    uint256 public override entryFee;
    /// @dev protocol revenue share in interest paid
    uint256 public override protocolShareFee;
    /// @dev protocol share in liquidation profit
    uint256 public protocolLiquidationFee;

    ITokensFactory public override tokensFactory;
    address public override oracle;
    address public override router;

    /// @dev version => IFactory
    mapping(uint256 => IFactory) public override siloFactory;

    uint256 public siloDefaultVersion;
    uint256 public siloLatestVersion;

    /// @dev maps token address to silo address, asset => silo
    mapping(address => address) public override silo;
    /// @dev maps token address to silo address, silo => asset
    mapping(address => address) public siloReverse;
    /// @dev maps silo => asset => config
    mapping(address => mapping(address => AssetConfig)) public assetConfigs;

    event NewSilo(address indexed silo, address indexed asset, uint256 siloVersion, address[] bridgeAssets);
    event BridgeAssetAdded(address newBridgeAsset);
    event BridgeAssetRemoved(address bridgeAssetRemoved);
    event InterestRateModel(address oldModel, address newModel);
    event BridgePoolUpdate(address oldPool, address newPool);
    event OracleUpdate(address oldOracle, address newOracle);
    event TokensFactoryUpdate(address oldTokensFactory, address newTokensFactory);
    event RouterUpdate(address oldRouter, address newRouter);
    event RegisterSiloVersion(address factory, uint256 siloLatestVersion, uint256 siloDefaultVersion);
    event UnregisterSiloVersion(address factory, uint256 siloVersion);
    event SiloDefaultVersion(uint256 siloDefaultVersion, uint256 defaultVersion);
    event FeeUpdate(
        uint256 oldEntryFee,
        uint256 newEntryFee,
        uint256 oldProtocolShareFee,
        uint256 newProtocolShareFee,
        uint256 oldProtocolLiquidationFee,
        uint256 newProtocolLiquidationFee
    );

    constructor(
        address _siloVerifier,
        address _factory,
        address _tokensFactory,
        uint256 _defaultMaxLTV,
        uint256 _defaultLiquidationThreshold,
        address[] memory _bridgeAssets
    ) GuardedLaunch(_siloVerifier) {
        require(_factory != address(0), "Repository.constructor: empty factory");
        require(_tokensFactory != address(0), "Repository.constructor: empty tokensFactory");
        require(_bridgeAssets.length != 0, "Repository.constructor: empty bridgeAssets");

        for (uint256 i = 0; i < _bridgeAssets.length; i++) {
            // this is instead of checking if address != 0
            ERC20(_bridgeAssets[i]).decimals();
        }

        siloDefaultVersion = 1;
        siloFactory[1] = IFactory(_factory);
        tokensFactory = ITokensFactory(_tokensFactory);

        defaultAssetConfig.maxLoanToValue = _defaultMaxLTV;
        defaultAssetConfig.liquidationThreshold = _defaultLiquidationThreshold;
        bridgeAssets = _bridgeAssets;
    }

    function setTokensFactory(address _tokensFactory) external onlyOwner {
        require(_tokensFactory != address(0), "Repository.setTokensFactory: empty tokensFactory");

        emit TokensFactoryUpdate(address(tokensFactory), _tokensFactory);
        tokensFactory = ITokensFactory(_tokensFactory);
    }

    function setFees(uint256 _entryFee, uint256 _protocolShareFee, uint256 _protocolLiquidationFee) external onlyOwner {
        emit FeeUpdate(
            entryFee,
            _entryFee,
            protocolShareFee,
            _protocolShareFee,
            protocolLiquidationFee,
            _protocolLiquidationFee
        );

        entryFee = _entryFee;
        protocolShareFee = _protocolShareFee;
        protocolLiquidationFee = _protocolLiquidationFee;
    }

    function setAssetConfig(
        address _silo,
        address _asset,
        uint256 _maxLoanToValue,
        uint256 _liquidationThreshold,
        address _interestRateModel
    ) external onlyOwner {
        assetConfigs[_silo][_asset] = AssetConfig(_maxLoanToValue, _liquidationThreshold, _interestRateModel);
    }

    function setDefaultInterestRateModel(address _defaultInterestRateModel) external onlyOwner {
        require(_defaultInterestRateModel != address(0), "Repository: new defaultInterestRateModel is zero address");
        emit InterestRateModel(defaultAssetConfig.interestRateModel, _defaultInterestRateModel);
        defaultAssetConfig.interestRateModel = _defaultInterestRateModel;
    }

    function setDefaultMaximumLTV(uint256 _defaultMaxLTV) external onlyOwner {
        defaultAssetConfig.maxLoanToValue = _defaultMaxLTV;
    }

    function setDefaultLiquidationThreshold(uint256 _defaultLiquidationThreshold) external onlyOwner {
        defaultAssetConfig.liquidationThreshold = _defaultLiquidationThreshold;
    }

    function setOracle(address _oracle) external onlyOwner {
        require(_oracle != address(0), "Repository: new oracle is zero address");
        emit OracleUpdate(oracle, _oracle);
        oracle = _oracle;
    }

    function setRouter(address _router) external onlyOwner {
        require(_router != address(0), "Repository: new router is zero address");
        emit RouterUpdate(router, _router);
        router = _router;
    }

    function addBridgeAsset(address _newBridgeAsset) external onlyOwner {
        if (bridgeAssets.length > 0) {
            require(
                bridgeAssetsIndex[_newBridgeAsset] == 0 && bridgeAssets[0] != _newBridgeAsset,
                "Repository: asset already added"
            );
        }

        bridgeAssetsIndex[_newBridgeAsset] = bridgeAssets.length;
        bridgeAssets.push(_newBridgeAsset);
        emit BridgeAssetAdded(_newBridgeAsset);

        _removeFromArray(removedBridgeAssetsIndexes, removedBridgeAssets, _newBridgeAsset);
    }

    function removeBridgeAsset(address _bridgeAssetToRemove) external onlyOwner {
        if (removedBridgeAssets.length != 0) {
            require(
                removedBridgeAssetsIndexes[_bridgeAssetToRemove] == 0 &&
                removedBridgeAssets[0] != _bridgeAssetToRemove,
                "Repository: asset already removed"
            );
        }

        removedBridgeAssetsIndexes[_bridgeAssetToRemove] = removedBridgeAssets.length;
        removedBridgeAssets.push(_bridgeAssetToRemove);
        emit BridgeAssetRemoved(_bridgeAssetToRemove);

        _removeFromArray(bridgeAssetsIndex, bridgeAssets, _bridgeAssetToRemove);
    }

    /// @inheritdoc IRepository
    function newSilo(
        address _siloAsset,
        uint256 _siloVersion,
        bytes memory _siloData,
        address _oracle,
        bytes calldata _oracleData
    ) external override {
        require(siloVerifier == address(0), "Repository: use newSiloVerified");

        _newSilo(_siloAsset, _siloVersion, _siloData, _oracle, _oracleData, false);
    }

    /// @inheritdoc IRepository
    function newSiloVerified(
        uint8 _v,
        bytes32 _r,
        bytes32 _s,
        address _siloAsset,
        uint256 _siloVersion,
        bytes memory _siloData,
        address _oracle,
        bytes calldata _oracleData
    )
        external
        override
    {
        require(
            siloOffChainVerification(_siloAsset, ISiloOracleRepository(oracle).quoteToken(), _v, _r, _s),
            "Repository: off-chain verification failed"
        );

        _newSilo(_siloAsset, _siloVersion, _siloData, _oracle, _oracleData, false);
    }

    /// @inheritdoc IRepository
    function replaceSilo(
        address _siloAsset,
        uint256 _siloVersion,
        bytes memory _siloData,
        address _oracle,
        bytes calldata _oracleData
    ) external override onlyOwner {
        require(siloVerifier == address(0), "Repository: use newSiloVerified");

        _newSilo(_siloAsset, _siloVersion, _siloData, _oracle, _oracleData, true);
    }

    function registerSiloVersion(IFactory _factory, bool _isDefault) external onlyOwner {
        uint256 newVersion = siloLatestVersion + 1;
        siloLatestVersion = newVersion;
        siloFactory[newVersion] = _factory;
        if (_isDefault) siloDefaultVersion = newVersion;

        emit RegisterSiloVersion(address(_factory), newVersion, siloDefaultVersion);
    }

    function unregisterSiloVersion(uint256 _siloVersion) external onlyOwner {
        emit UnregisterSiloVersion(address(siloFactory[_siloVersion]), _siloVersion);
        siloFactory[_siloVersion] = IFactory(address(0));
    }

    function setDefaultSiloVersion(uint256 _defaultVersion) external onlyOwner {
        require(_defaultVersion <= siloLatestVersion, "Repository: version does not exist");
        emit SiloDefaultVersion(siloDefaultVersion, _defaultVersion);
        siloDefaultVersion = _defaultVersion;
    }

    function isSilo(address _silo) external view override returns (bool) {
        return siloReverse[_silo] != address(0);
    }

    function getBridgeAssets() external view override returns (address[] memory) {
        return bridgeAssets;
    }

    function getRemovedBridgeAssets() external view override returns (address[] memory) {
        return removedBridgeAssets;
    }

    function getMaximumLTV(address _silo, address _asset) external view override returns (uint256) {
        uint256 maxLoanToValue = assetConfigs[_silo][_asset].maxLoanToValue;
        if (maxLoanToValue != 0) {
            return maxLoanToValue;
        }
        return defaultAssetConfig.maxLoanToValue;
    }

    function getInterestRateModel(address _silo, address _asset) external view override returns (address) {
        address interestRateModel = assetConfigs[_silo][_asset].interestRateModel;
        if (interestRateModel != address(0)) {
            return interestRateModel;
        }
        return defaultAssetConfig.interestRateModel;
    }

    function getLiquidationThreshold(address _silo, address _asset) external view override returns (uint256) {
        uint256 liquidationThreshold = assetConfigs[_silo][_asset].liquidationThreshold;

        if (liquidationThreshold != 0) {
            return liquidationThreshold;
        }

        return defaultAssetConfig.liquidationThreshold;
    }

    function owner() public view override(IRepository, TwoStepOwnable) returns (address) {
        return super.owner();
    }

    /// @dev Deploys latest default Silo
    /// @param _siloAsset silo asset
    /// @param _siloVersion version of silo implementation
    /// @param _siloData (optional) data that may be needed during silo creation
    /// @param _oracle (optional) if provided, asset will be initialised on that oracle
    ///        it does not mean, this oracle will be chosen for asset, it only means, after valid initialization,
    ///        it could be chosen, if is is the best one
    /// @param _oracleData custom data for oracle initialization
    function _newSilo(
        address _siloAsset,
        uint256 _siloVersion,
        bytes memory _siloData,
        address _oracle,
        bytes calldata _oracleData,
        bool _replace
    )
        internal
        isPermissionedSiloCreation
    {
        // 0 means default version
        if (_siloVersion == 0) _siloVersion = siloDefaultVersion;
        address[] memory _bridgeAssets = bridgeAssets;

        // if replacing, do not check for duplicates
        if (!_replace) _requireNoSiloDuplicates(_bridgeAssets, _siloAsset);

        address _silo = siloFactory[_siloVersion].createSilo(_siloAsset, _siloData);
        Silo(_silo).syncBridgeAssets();

        silo[_siloAsset] = _silo;
        siloReverse[_silo] = _siloAsset;
        emit NewSilo(_silo, _siloAsset, _siloVersion, _bridgeAssets);

        ISiloOracleRepository(oracle).initOracleForAsset(_siloAsset, _oracle, _oracleData);
    }

    function _removeFromArray(
        mapping(address => uint256) storage _indexes,
        address[] storage _array,
        address _asset
    ) internal {
        uint256 _assetIndex = _indexes[_asset];
        uint256 arrayLength = _array.length;

        if (_array[_assetIndex] != _asset || arrayLength == 0) return;

        uint256 lastIndex = arrayLength - 1;

        if (_assetIndex != lastIndex) {
            address assetAddr = _array[lastIndex];
            _array[_assetIndex] = assetAddr;
            _indexes[assetAddr] = _assetIndex;
        }

        delete _indexes[_asset];
        _array.pop();
    }

    /// @dev Revert if Silo Version is already deployed
    function _requireNoSiloDuplicates(address[] memory _bridgeAssets, address _siloAsset) internal view {
        // Check if there's more than one bridge asset and if _siloAsset is bridge asset
        if (_bridgeAssets.length > 1 && _bridgeAssets[bridgeAssetsIndex[_siloAsset]] == _siloAsset) {
            for (uint256 i = 0; i < _bridgeAssets.length; i++) {
                // make sure there isn't a bridge assets silo deployed already
                if (siloReverse[_bridgeAssets[i]] != address(0)) {
                    revert BridgeSiloExists(silo[_bridgeAssets[i]]);
                }
            }
        } else {
            require(
                bridgeAssets[bridgeAssetsIndex[_siloAsset]] != _siloAsset,
                "Repository: cannot create Silo for single bridge asset"
            );
        }

        require(silo[_siloAsset] == address(0), "Repository: silo exists");
    }
}

contracts/utils/GuardedLaunch.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import "../interfaces/IGuardedLaunch.sol";
import "./TwoStepOwnable.sol";

contract GuardedLaunch is IGuardedLaunch, TwoStepOwnable {
    /// @dev SiloLiquidityConfig struct represents maximum allowed deposit limits for each Silo
    struct SiloLimitConfig {
        uint256 maxDeposits;
        uint256 maxBridgeDeposits;
    }

    uint256 public constant INFINITY = type(uint256).max;
    bytes constant public ETH_PREFIX = "\x19Ethereum Signed Message:\n32";

    /// @dev flag for guarded functionality
    bool public guardedLaunch;
    /// @dev flag to limit permissionless Silo creation
    bool public permissionedSiloCreation;
    /// @dev flag to limit max liquidity on Silos
    bool public limitedMaxLiquidity;
    SiloLimitConfig public defaultSiloLimitConfig;

    /// @dev this wallet provides signature, that means, all required off-chain checks are done for new silo
    address public siloVerifier;

    mapping(address => SiloLimitConfig) public siloLimitConfigs;

    event SiloVerifier(address verifier);
    event GuardedLaunchToggled(bool newGuardedLaunchState);
    event PermissionedSiloCreationToggled(bool newPermissionedSiloCreationState);
    event LimitedMaxLiquidityToggled(bool newLimitedMaxLiquidityState);
    event SiloLimitConfigUpdate(address indexed silo, uint256 newMaxDeposits, uint256 newMaxBridgeDeposits);

    modifier isPermissionedSiloCreation() {
        require(!(guardedLaunch && permissionedSiloCreation) || owner() == msg.sender, "Silo:only owner can perform");

        _;
    }

    constructor(address _siloVerifier) {
        require(_siloVerifier != address(0), "empty siloVerifier");

        siloVerifier = _siloVerifier;

        /// @dev All guarded launch flags are turned ON by default
        guardedLaunch = true;
        permissionedSiloCreation = true;
        limitedMaxLiquidity = true;

        /// @dev Default values for SiloLiquidityConfig are both 50 ETH
        defaultSiloLimitConfig.maxDeposits = 50 ether;
        defaultSiloLimitConfig.maxBridgeDeposits = 50 ether;
    }

    /// @param _verifier off-chain verifier address, if set to address(0) then verification is off
    function setVerifier(address _verifier) external onlyOwner {
        siloVerifier = _verifier;
        emit SiloVerifier(_verifier);
    }

    function toggleGuardedLaunch() external onlyOwner {
        guardedLaunch = !guardedLaunch;
        emit GuardedLaunchToggled(guardedLaunch);
    }

    function togglePermissionedSiloCreation() external onlyOwner {
        permissionedSiloCreation = !permissionedSiloCreation;
        emit PermissionedSiloCreationToggled(permissionedSiloCreation);
    }

    function toggleLimitedMaxLiquidity() external onlyOwner {
        limitedMaxLiquidity = !limitedMaxLiquidity;
        emit LimitedMaxLiquidityToggled(limitedMaxLiquidity);
    }

    function setSiloLimitConfig(
        address _silo,
        uint256 _maxDeposits,
        uint256 _maxBridgeDeposits
    ) external onlyOwner {
        siloLimitConfigs[_silo] = SiloLimitConfig(
            _maxDeposits,
            _maxBridgeDeposits
        );
        emit SiloLimitConfigUpdate(_silo, _maxDeposits, _maxBridgeDeposits);
    }

    function getMaxSiloDeposits(address _silo) external view override returns (uint256) {
        if (guardedLaunch && limitedMaxLiquidity) {
            uint256 maxDeposits = siloLimitConfigs[_silo].maxDeposits;
            if (maxDeposits != 0) {
                return maxDeposits;
            }
            return defaultSiloLimitConfig.maxDeposits;
        }
        return INFINITY;
    }

    function getMaxSiloBridgeDeposits(address _silo) external view override returns (uint256) {
        if (guardedLaunch && limitedMaxLiquidity) {
            uint256 maxBridgeDeposits = siloLimitConfigs[_silo].maxBridgeDeposits;
            if (maxBridgeDeposits != 0) {
                return maxBridgeDeposits;
            }
            return defaultSiloLimitConfig.maxBridgeDeposits;
        }
        return INFINITY;
    }

    /// @dev checks if off-chain verifier sign allowance to create silo
    ///         verifier signature if valid for an indefinite period
    function siloOffChainVerification(
        address _asset,
        address _quote,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) public view returns (bool) {
        return recoverSigner(_asset, _quote, _v, _r, _s) == siloVerifier;
    }

    function recoverSigner(address _asset, address _quote, uint8 _v, bytes32 _r, bytes32 _s)
        public
        pure
        returns (address)
    {
        bytes32 hash = keccak256(abi.encodePacked(ETH_PREFIX, _asset, _quote));
        return ecrecover(hash, _v, _r, _s);
    }
}

contracts/utils/TwoStepOwnable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.7;

abstract contract TwoStepOwnable {
    address private _owner;
    address private _pendingOwner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
    event OwnershipPending(address indexed previousOwner, address indexed newOwner);

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(owner() == msg.sender, "Ownable: caller is not the owner");
        _;
    }

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor() {
        _setOwner(msg.sender);
    }

    /**
     * @dev Leaves the contract without owner. It will not be possible to call
     * `onlyOwner` functions anymore. Can only be called by the current owner.
     *
     * NOTE: Renouncing ownership will leave the contract without an owner,
     * thereby removing any functionality that is only available to the owner.
     */
    function renounceOwnership() public virtual onlyOwner {
        _setOwner(address(0));
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        _setOwner(newOwner);
    }

    /**
     * @dev Transfers pending ownership of the contract to a new account (`newPendingOwner`).
     * Can only be called by the current owner.
     */
    function transferPendingOwnership(address newPendingOwner) public virtual onlyOwner {
        emit OwnershipPending(owner(), newPendingOwner);
        _pendingOwner = newPendingOwner;
    }

    /**
     * @dev Transfers ownership of the contract to a pending owner
     * Can only be called by the pending owner.
     */
    function acceptOwnership() public virtual {
        require(msg.sender == pendingOwner(), "Ownable: caller is not the pendingOwner");
        _setOwner(pendingOwner());
        _pendingOwner = address(0);
    }

    /**
     * @dev Returns the address of the current owner.
     */
    function owner() public view virtual returns (address) {
        return _owner;
    }

    /**
     * @dev Returns the address of the pending owner.
     */
    function pendingOwner() public view virtual returns (address) {
        return _pendingOwner;
    }

    function _setOwner(address newOwner) private {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}

