// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import "./utils/ShareCollateralToken.sol";
import "./utils/ShareDebtToken.sol";
import "./interfaces/ITokensFactory.sol";

contract TokensFactory is ITokensFactory {
