        string memory _name,
        string memory _symbol,
        address _asset
    )
        external
        override
        returns (IShareToken token)
    {
        token = new ShareDebtToken(_name, _symbol, msg.sender, _asset);
        emit NewShareDebtTokenCreated(address(token));
    }
}

contracts/utils/ShareCollateralToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.7;

import "./ShareToken.sol";
import "../interfaces/ISilo.sol";
import "../Error.sol";

contract ShareCollateralToken is ShareToken {
    constructor (
        string memory _name,
        string memory _symbol,
        address _silo,
        address _asset
    ) ERC20(_name, _symbol) ShareToken(_silo, _asset) {}

    function _beforeTokenTransfer(address _sender, address _recipient, uint256) internal view override {
        // if we minting or burning, Silo is responsible to check all necessary conditions
        if (!_isTransfer(_sender, _recipient)) {
            return;
        }

        if (!silo.depositPossible(asset, _recipient)) {
            revert ShareTransferNotAllowed();
        }
    }

    function _afterTokenTransfer(address _sender, address _recipient, uint256) internal view override {
        // if we minting or burning, Silo is responsible to check all necessary conditions
        if (_isTransfer(_sender, _recipient)) {
            require(silo.isSolvent(_sender), "Transfer Collateral: sender not solvent after transfer");
        }
    }
}

contracts/utils/ShareDebtToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.7;

import "./ShareToken.sol";
import "./ERC20R.sol";

import "../interfaces/ISilo.sol";
import "../Error.sol";

contract ShareDebtToken is ShareToken, ERC20R {
    constructor (
        string memory _name,
        string memory _symbol,
        address _silo,
        address _asset
    ) ERC20R(_name, _symbol) ShareToken(_silo, _asset) {}

    function symbol() public view override(ShareToken, ERC20) returns (string memory) {
        return ERC20.symbol();
    }

    function _beforeTokenTransfer(address _sender, address _recipient, uint256 _amount) internal override {
        // if we minting or burning, Silo is responsible to check all necessary conditions
        if (!_isTransfer(_sender, _recipient)) {
            return;
        }

        if (!silo.borrowPossible(asset, _recipient)) {
            revert ShareTransferNotAllowed();
        }

        uint256 currentAllowance = receiveAllowance(_sender, _recipient);
        require(currentAllowance >= _amount, "ERC20: transfer debt amount exceeds allowance");

        _setReceiveApproval(_sender, _recipient, currentAllowance - _amount);
    }

    function _afterTokenTransfer(address _sender, address _recipient, uint256) internal view override {
        // if we minting or burning, Silo is responsible to check all necessary conditions
        if (_isTransfer(_sender, _recipient)) {
            // if we not minting and not burning, means we are transferring
            require(silo.isSolvent(_recipient), "Transfer Debt: recipient not solvent after transfer");
        }
    }
}

contracts/utils/ShareToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.7;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

import "../interfaces/ISilo.sol";
import "../interfaces/IShareToken.sol";
import "../Error.sol";

abstract contract ShareToken is ERC20, IShareToken {
    ISilo public immutable silo;

    /// @dev asset for which this tokens is deployed
    address public immutable asset;

    modifier onlySilo {
        if (msg.sender != address(silo)) {
            revert AccessDenied();
        }

        _;
    }

    constructor(address _silo, address _asset) {
        silo = ISilo(_silo);
        asset = _asset;
    }

    function mint(address _account, uint256 _amount) external onlySilo override {
        _mint(_account, _amount);
    }

    function burn(address _account, uint256 _amount) external onlySilo override {
        _burn(_account, _amount);
    }

    function symbol() public view virtual override(IShareToken, ERC20) returns (string memory) {
        return ERC20.symbol();
    }

    /// @dev checks if operation is "real" transfer
    /// @param _sender sender address
    /// @param _recipient recipient address
    /// @return bool true if operation is real transfer, false if it is mint or burn
    function _isTransfer(address _sender, address _recipient) internal pure returns (bool) {
        // in order this check to be true, is is required to have:
        // require(sender != address(0), "ERC20: transfer from the zero address");
        // require(recipient != address(0), "ERC20: transfer to the zero address");
        // on transfer. ERC20Upgradeable has them, so we good.
        return _sender != address(0) && _recipient != address(0);
    }
}

contracts/utils/ERC20R.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.7;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "../interfaces/IERC20R.sol";

contract ERC20R is ERC20, IERC20R {
    mapping(address => mapping(address => uint256)) private _receiveAllowances;

    constructor(string memory _name, string memory _symbol) ERC20(_name, _symbol) {}

    // TODO approve and call?

    /// @inheritdoc IERC20R
    function setReceiveApproval(address owner, uint256 _amount) external virtual override returns (bool) {
        _setReceiveApproval(owner, _msgSender(), _amount);
        return true;
    }

    /// @inheritdoc IERC20R
    function decreaseReceiveAllowance(address _owner, uint256 _subtractedValue) public virtual override returns (bool) {
        uint256 currentAllowance = _receiveAllowances[_owner][_msgSender()];

        unchecked {
            uint256 newAllowance = currentAllowance <= _subtractedValue ? 0 : currentAllowance - _subtractedValue;
            _setReceiveApproval(_owner, _msgSender(), newAllowance);
        }

        return true;
    }

    /// @inheritdoc IERC20R
    function increaseReceiveAllowance(address _owner, uint256 _addedValue) public virtual override returns (bool) {
        uint256 currentAllowance = _receiveAllowances[_owner][_msgSender()];

        unchecked {
            uint256 newAllowance = type(uint256).max - currentAllowance <= _addedValue
                    ? type(uint256).max
                    : currentAllowance + _addedValue;

            _setReceiveApproval(_owner, _msgSender(), newAllowance);
        }

        return true;
    }

    /// @inheritdoc IERC20R
    function receiveAllowance(address owner, address receiver) public view virtual override returns (uint256) {
        return _receiveAllowances[owner][receiver];
    }

    function _setReceiveApproval(
        address _owner,
        address _receiver,
        uint256 _amount
    ) internal virtual {
        require(_owner != address(0), "ERC20R: changeReceiveApproval from the zero address");
        require(_receiver != address(0), "ERC20R: changeReceiveApproval to the zero address");

        _receiveAllowances[_owner][_receiver] = _amount;

        emit ReceiveApproval(_owner, _receiver, _amount);
    }
}

