// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import "./Silo.sol";
import "./interfaces/IFactory.sol";

contract Factory is IFactory {
    event NewSiloCreated(address indexed silo, address indexed asset);

    /// @inheritdoc IFactory
    function createSilo(address _siloAsset, bytes memory) external override returns (address silo) {
        silo = address(new Silo(msg.sender, _siloAsset));
        emit NewSiloCreated(silo, _siloAsset);
    }
}
