contracts/SiloRouter.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import "./interfaces/IWrappedNativeToken.sol";
import "./interfaces/ISilo.sol";
import "./interfaces/ISiloRepository.sol";

import "./lib/Ping.sol";

/// @title SiloRouter
/// @notice Silo Router is a utility contract that aims to improve UX. It can batch any number or combination
/// of actions (Deposit, Withdraw, Borrow, Repay) and execute them in a single transaction.
/// @dev SiloRouter requires only first action asset to be approved
/// @custom:security-contact security@silo.finance
contract SiloRouter is ReentrancyGuard {
    using SafeERC20 for IERC20;

    // @notice Action types that are supported
    enum ActionType { Deposit, Withdraw, Borrow, Repay }

    struct Action {
        // what do you want to do?
        ActionType actionType;
        // which Silo are you internacting with?
        ISilo silo;
        // what asset do you want to use?
        IERC20 asset;
        // how much asset do you want to use?
        uint256 amount;
        // is it an action on collateral only?
        bool collateralOnly;
    }

    // @dev native asset wrapped token. In case of Ethereum, it's WETH.
    IWrappedNativeToken public immutable wrappedNativeToken;

    constructor (address _wrappedNativeToken) {
        require(Ping.decimals(_wrappedNativeToken) != 0, "invalid _wrappedNativeToken");

        wrappedNativeToken = IWrappedNativeToken(_wrappedNativeToken);
    }

    /// @dev needed for unwrapping WETH
    receive() external payable {}

    /// @notice Execute actions
    /// @dev User can bundle any combination and number of actions. It's possible to do multiple deposits,
    /// withdraws etc. For that reason router may need to send multiple tokens back to the user.
    /// @param _actions array of actions to execute
    function execute(Action[] calldata _actions) external payable nonReentrant {
        uint256 len = _actions.length;

        // execute actions
        for (uint256 i = 0; i < len; i++) {
            _executeAction(_actions[i]);
        }

        // send all assets to user
        for (uint256 i = 0; i < len; i++) {
            uint256 remainingBalance = _actions[i].asset.balanceOf(address(this));

            if (remainingBalance != 0) {
                _sendAsset(_actions[i].asset, remainingBalance);
            }
        }

        // should never have leftover ETH, however
        if (address(this).balance != 0) {
            // solhint-disable-next-line avoid-low-level-calls
            (bool success, ) = msg.sender.call{value: address(this).balance}("");
            require(success, "SiloRouter: ETH transfer failed.");
        }
    }

    function siloRouterPing() external pure returns (bool) {
        return true;
    }

    /// @dev Execute actions
    /// @param _action action to execute, this can be one of many actions in the whole flow
    // solhint-disable-next-line code-complexity
    function _executeAction(Action calldata _action) internal {
        if (_action.actionType == ActionType.Deposit) {
            uint256 remainingBalance = _action.asset.balanceOf(address(this));

            if (remainingBalance < _action.amount) {
                _pullAsset(address(_action.asset), _action.amount - remainingBalance);
            }

            _approveIfNeeded(_action.asset, address(_action.silo), _action.amount);

            _action.silo.depositFor(address(_action.asset), msg.sender, _action.amount, _action.collateralOnly);
        } else if (_action.actionType == ActionType.Withdraw) {
            _action.silo.withdrawFor(
                address(_action.asset),
                msg.sender,
                address(this),
                _action.amount,
                _action.collateralOnly
            );
        } else if (_action.actionType == ActionType.Borrow) {
            _action.silo.borrowFor(address(_action.asset), msg.sender, address(this), _action.amount);
        } else if (_action.actionType == ActionType.Repay) {
            uint256 remainingBalance = _action.asset.balanceOf(address(this));

            if (remainingBalance < _action.amount) {
                _pullAsset(address(_action.asset), _action.amount - remainingBalance);
            }

            _approveIfNeeded(_action.asset, address(_action.silo), _action.amount);

            _action.silo.repayFor(address(_action.asset), msg.sender, _action.amount);
        } else {
            revert("SiloRouter: unsupported action");
        }
    }

    /// @dev Approve Silo to transfer token if current allowance is not enough
    /// @param _token token to be approved
    /// @param _spender Silo address that spends the token
    /// @param _value amount of token to be spent
    function _approveIfNeeded(
        IERC20 _token,
        address _spender,
        uint256 _value
    ) internal {
        if (_token.allowance(address(this), _spender) < _value) {
            _token.safeApprove(_spender, type(uint256).max);
        }
    }

    /// @dev Transfer asset from user to router
    /// @param _asset asset address to be transferred
    /// @param _amount amount of asset to be transferred
    function _pullAsset(address _asset, uint256 _amount) internal {
        if (msg.value != 0 && _asset == address(wrappedNativeToken)) {
            wrappedNativeToken.deposit{value: _amount}();
        } else {
            IERC20(_asset).safeTransferFrom(msg.sender, address(this), _amount);
        }
    }

    /// @dev Transfer asset from router to user
    /// @param _asset asset address to be transferred
    /// @param _amount amount of asset to be transferred
    function _sendAsset(IERC20 _asset, uint256 _amount) internal {
        if (address(_asset) == address(wrappedNativeToken)) {
            wrappedNativeToken.withdraw(_amount);
            // solhint-disable-next-line avoid-low-level-calls
            (bool success, ) = msg.sender.call{value: _amount}("");
            require(success, "SiloRouter: asset transfer failed.");
        } else {
            _asset.safeTransfer(msg.sender, _amount);
        }
    }
}

contracts/utils/GuardedLaunch.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import "../interfaces/IGuardedLaunch.sol";
import "./TwoStepOwnable.sol";

/// @title GuardedLaunch
/// @notice Implements security and risk averse functions for Silo
/// @dev This contract is meant to limit Silo functionality for Beta Release in order to minimize any damage
/// of potential critical vulnerability.
/// @custom:security-contact security@silo.finance
contract GuardedLaunch is IGuardedLaunch, TwoStepOwnable {
    uint256 private constant _INFINITY = type(uint256).max;

    /// @dev stores max liquidity config
    MaxLiquidityLimit public maxLiquidity;

    /// @dev stores pause config
    Paused public isPaused;

    constructor() {
        maxLiquidity.globalToggle = true;

        /// @dev Default value for defaultMaxLiquidity is 250 ETH
        maxLiquidity.defaultMaxLiquidity = 250 ether;
    }

    /// @inheritdoc IGuardedLaunch
    function toggleLimitedMaxLiquidity() external onlyOwner override {
        maxLiquidity.globalToggle = !maxLiquidity.globalToggle;
        emit LimitedMaxLiquidityToggled(maxLiquidity.globalToggle);
    }

    /// @inheritdoc IGuardedLaunch
    function setDefaultSiloMaxDepositsLimit(uint256 _maxDeposits) external onlyOwner override {
        maxLiquidity.defaultMaxLiquidity = _maxDeposits;
        emit DefaultSiloMaxDepositsLimitUpdate(_maxDeposits);
    }

    /// @inheritdoc IGuardedLaunch
    function setSiloMaxDepositsLimit(
        address _silo,
        address _asset,
        uint256 _maxDeposits
    ) external onlyOwner override {
        maxLiquidity.siloMaxLiquidity[_silo][_asset] = _maxDeposits;
        emit SiloMaxDepositsLimitsUpdate(_silo, _asset, _maxDeposits);
    }

    /// @inheritdoc IGuardedLaunch
    function setGlobalPause(bool _globalPause) external onlyOwner override {
        isPaused.globalPause = _globalPause;
        emit GlobalPause(_globalPause);
    }

    /// @inheritdoc IGuardedLaunch
    function setSiloPause(address _silo, address _asset, bool _pauseValue) external onlyOwner override {
        isPaused.siloPause[_silo][_asset] = _pauseValue;
        emit SiloPause(_silo, _asset, _pauseValue);
    }

    /// @inheritdoc IGuardedLaunch
    function isSiloPaused(address _silo, address _asset) external view override returns (bool) {
        return isPaused.globalPause || isPaused.siloPause[_silo][address(0)] || isPaused.siloPause[_silo][_asset];
    }

    /// @inheritdoc IGuardedLaunch
    function getMaxSiloDeposits(address _silo, address _asset) external view override returns (uint256) {
        if (maxLiquidity.globalToggle) {
            uint256 maxDeposits = maxLiquidity.siloMaxLiquidity[_silo][_asset];
            if (maxDeposits != 0) {
                return maxDeposits;
            }
            return maxLiquidity.defaultMaxLiquidity;
        }
        return _INFINITY;
    }
}

contracts/interfaces/IWrappedNativeToken.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IWrappedNativeToken is IERC20 {
    function deposit() external payable;
    function withdraw(uint256 amount) external;
}

contracts/utils/TwoStepOwnable.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

/// @title TwoStepOwnable
/// @notice Contract that implements the same functionality as popular Ownable contract from openzeppelin library.
/// The only difference is that it adds a possibility to transfer ownership in two steps. Single step ownership
/// transfer is still supported.
/// @dev Two step ownership transfer is meant to be used by humans to avoid human error. Single step ownership
/// transfer is meant to be used by smart contracts to avoid over-complicated two step integartion. For that reason,
/// both ways are supported.
/// @custom:security-contact security@silo.finance
abstract contract TwoStepOwnable {
    /// @notice current owner
    address private _owner;
    /// @notice candidate to an owner
    address private _pendingOwner;

    /// @notice Emitted when ownership is transferred on `transferOwnership` and `acceptOwnership`
    /// @param previousOwner previous owner
    /// @param newOwner new owner
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
    /// @notice Emitted when ownership transfer is proposed, aka pending owner is set
    /// @param previousOwner current owner
    /// @param newOwner new proposed/pending owner
    event OwnershipPending(address indexed previousOwner, address indexed newOwner);

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(owner() == msg.sender, "Ownable: caller is not the owner");
        _;
    }

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor() {
        _setOwner(msg.sender);
    }

    /**
     * @dev Leaves the contract without owner. It will not be possible to call
     * `onlyOwner` functions anymore. Can only be called by the current owner.
     *
     * NOTE: Renouncing ownership will leave the contract without an owner,
     * thereby removing any functionality that is only available to the owner.
     */
    function renounceOwnership() public virtual onlyOwner {
        _setOwner(address(0));
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        _setOwner(newOwner);
    }

    /**
     * @dev Transfers pending ownership of the contract to a new account (`newPendingOwner`).
     * Can only be called by the current owner.
     */
    function transferPendingOwnership(address newPendingOwner) public virtual onlyOwner {
        emit OwnershipPending(owner(), newPendingOwner);
        _pendingOwner = newPendingOwner;
    }

    /**
     * @dev Transfers ownership of the contract to a pending owner
     * Can only be called by the pending owner.
     */
    function acceptOwnership() public virtual {
        require(msg.sender == pendingOwner(), "Ownable: caller is not the pendingOwner");
        _setOwner(pendingOwner());
        _pendingOwner = address(0);
    }

    /**
     * @dev Returns the address of the current owner.
     */
    function owner() public view virtual returns (address) {
        return _owner;
    }

    /**
     * @dev Returns the address of the pending owner.
     */
    function pendingOwner() public view virtual returns (address) {
        return _pendingOwner;
    }

    function _setOwner(address newOwner) private {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}

