pragma solidity 0.8.7;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/// @dev this is MOCK contract - DO NOT USE IT!
contract MockERC20 is ERC20 {
    constructor() ERC20("mock ERC20", "mock ERC20") {
    }

    function mint(address _holder, uint256 _amount) external {
        _mint(_holder, _amount);
    }

    function burn(address _holder) external {
        _burn(_holder, balanceOf(_holder));
    }

    /// @dev our goal is not test ERC20, so let's make our life a bit easier
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        if (from == address(0) || to == address(0)) return;

        _approve(from, to, amount);
    }

}

contracts/governance/SiloSnapshotWrapper.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

import "./TreasuryVester.sol";

/// @title Silo Snapshot token
/// @notice Utility Silo tokens that adds unvested Silo tokens to account balance. It's used for snapshot voting
///         to allow unvested tokens to vote. It's not transferable.
/// @custom:security-contact security@silo.finance
contract SiloSnapshotWrapper is ERC20 {
    ERC20 public siloToken;
    address[] public vestingContracts;

    constructor(address _siloToken, address[] memory _vestingContracts) ERC20("Silo Snapshot Token", "snapshotSILO") {
        require(_siloToken != address(0), "empty _siloToken");

        for (uint256 i; i < _vestingContracts.length; i++) {
            require(_vestingContracts[i] != address(0), "empty address in _vestingContracts");
        }

        siloToken = ERC20(_siloToken);
        vestingContracts = _vestingContracts;
    }

    /// @inheritdoc IERC20
    function totalSupply() public view override returns (uint256) {
        return siloToken.totalSupply();
    }

    /// @inheritdoc IERC20
    function balanceOf(address _account) public view override returns (uint256) {
        return getCurrentBalance(_account) + getVestingBalance(_account);
    }

    /// @notice Calculates amount of token that are vesting for account
    /// @param _account address of account for which to calcualte amount of tokens
    /// @return userBalance amount of tokens vesting
    function getVestingBalance(address _account) public view returns (uint256 userBalance) {
        for (uint256 i = 0; i < vestingContracts.length; i++) {
            if (TreasuryVester(vestingContracts[i]).recipient() == _account) {
                userBalance += siloToken.balanceOf(vestingContracts[i]);
            }
        }
    }

    /// @notice Current token balance of account
    /// @param _account address of account for which to calcualte current balance
    /// @return current balance
    function getCurrentBalance(address _account) public view returns (uint256) {
        return siloToken.balanceOf(_account);
    }

    /// @inheritdoc ERC20
    function _beforeTokenTransfer(address, address, uint256) internal pure override {
        revert("Non-transferable");
    }
}

contracts/governance/TreasuryVester.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import "@openzeppelin/contracts/access/Ownable.sol";

interface IToken {
    function transfer(address dst, uint256 rawAmount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
}

/// @notice Vesting contract for Silo token allocaitons
/// @dev Forked from https://github.com/Uniswap/governance/blob/master/contracts/TreasuryVester.sol
/// @custom:security-contact security@silo.finance
contract TreasuryVester is Ownable {
    /// @notice silo token address
    address public immutable siloToken;
    /// @notice wallet address that is vesting token allcoation
    address public recipient;

    /// @notice amount of token that is being allocated for vesting
    uint256 public immutable vestingAmount;
    /// @notice timestamp of vesting start date
    uint256 public immutable vestingBegin;
    /// @notice timestamp of vesting cliff, aka. the time before which token cannot be claimed
    uint256 public immutable vestingCliff;
    /// @notice timestamp of vesting end date
    uint256 public immutable vestingEnd;
    /// @notice can it be revoked by owner
    bool public immutable revocable;

    /// @notice timestamp of last claim
    uint256 public lastUpdate;
    /// @notice set to true if vesting has been revoked
    bool public revoked;

    /// @param _siloToken silo token address
    /// @param _recipient wallet address that is vesting token allcoation
    /// @param _vestingAmount amount of token that is being allocated for vesting
    /// @param _vestingBegin timestamp of vesting start date
    /// @param _vestingCliff timestamp of vesting cliff, aka. the time before which token cannot be claimed
    /// @param _vestingEnd timestamp of vesting end date
    /// @param _revocable can it be revoked by owner
    constructor(
        address _siloToken,
        address _recipient,
        uint256 _vestingAmount,
        uint256 _vestingBegin,
        uint256 _vestingCliff,
        uint256 _vestingEnd,
        bool _revocable
    ) {
        require(_vestingBegin >= block.timestamp, "TreasuryVester::constructor: vesting begin too early");
        require(_vestingCliff >= _vestingBegin, "TreasuryVester::constructor: cliff is too early");
        require(_vestingEnd > _vestingCliff, "TreasuryVester::constructor: end is too early");

        siloToken = _siloToken;
        recipient = _recipient;

        vestingAmount = _vestingAmount;
        vestingBegin = _vestingBegin;
        vestingCliff = _vestingCliff;
        vestingEnd = _vestingEnd;

        lastUpdate = _vestingBegin;

        revocable = _revocable;
    }

    /// @notice allows current recipient to update vesting wallet
    /// @param _recipient new wallet address that is going to vest token allcoation
    function setRecipient(address _recipient) external {
        require(msg.sender == recipient, "TreasuryVester::setRecipient: unauthorized");
        recipient = _recipient;
    }

    /// @notice revokes vesting
    /// @dev calls claim() to sent already vested token. The remaining is returned to the owner.
    function revoke() external onlyOwner {
