// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import "./Silo.sol";
import "./interfaces/ISiloFactory.sol";

/// @title SiloFactory
/// @notice Silo Factory has one job, deploy Silo implementaiton
/// @dev Silo Factory is registered within SiloRepository contract and it's given a version. Each version
/// is differet Silo Factory that deploys different Silo implementation. Many Factory contracts can be
/// registered with the Repository contract.
/// @custom:security-contact security@silo.finance
contract SiloFactory is ISiloFactory {
    /// @inheritdoc ISiloFactory
    function createSilo(address _siloAsset, bytes memory) external override returns (address silo) {
        // disallow EOA calls to minimize non-SiloRepository Silo deployments, just for esthetics
        // solhint-disable-next-line avoid-tx-origin
        require(tx.origin != msg.sender, "SiloFactory: no pollution!");

        silo = address(new Silo(msg.sender, _siloAsset));
        emit NewSiloCreated(silo, _siloAsset);
    }

    function siloFactoryPing() external pure override returns (bool) {
        return true;
